# frozen_string_literal: true

require "debug"

input = <<~INPUT
  MMMSXXMASM
  MSAMXMSMSA
  AMXSXMAAMM
  MSAMASMSMX
  XMASAMXAMM
  XXAMMXXAMA
  SMSMSASXSS
  SAXAMASAAA
  MAMMMXMMMM
  MXMXAXMASX
INPUT

def count_words(row)
  row.each_cons(4).count { %w[XMAS SAMX].include?(_1.join) }
end

# Rotates a 2D array by 45 deg, producing diagonals
def rotate(rows)
  padding = [*0..(rows.length - 1)].map { |i| [nil] * i }

  padding.reverse
         .zip(rows)
         .zip(padding)
         .map(&:flatten)
         .transpose
         .map(&:compact)
end

input = DATA.read
rows  = input.split("\n").map(&:chars)
cols  = rows.transpose

puts [
  rows.sum { count_words _1 },
  cols.sum { count_words _1 },
  rotate(rows).sum { count_words _1 }, # First Diagonal
  rotate(rows.map(&:reverse).transpose).sum { count_words _1 } # Second Diagonal
].sum

__END__
